package day02Practice;

public class Demo2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 1;
		//利用while循环来实现叠加功能
		while(i<=100){
			System.out.println(i);
			i++;
		}
		System.out.println("循环过后i的值为："+i);
	}
	
	/**
	 * @param none
	 */
}
